import React, { useState, useEffect } from 'react';
import { collection, onSnapshot, query, where, getDocs } from 'firebase/firestore';
import { firestore } from '../firebase';

function Slider({ currentUserId, selectUserToChat, selectedUserId }) {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Listen for accepted invitations where the current user is the inviter
    const q = query(
      collection(firestore, 'requests'),
      where('fromUserId', '==', currentUserId),
      where('status', '==', 'accepted')
    );

    const unsubscribe = onSnapshot(q, async (snapshot) => {
      const acceptedUserIds = snapshot.docs.map(doc => doc.data().toUserId);

      // Fetch user data based on accepted invitations
      const usersCollection = collection(firestore, 'users');
      const userDocs = await getDocs(usersCollection);
      const fetchedUsers = userDocs.docs
        .filter(doc => acceptedUserIds.includes(doc.id))
        .map(doc => ({ id: doc.id, ...doc.data() }));

      setUsers(fetchedUsers);
    });

    return () => unsubscribe();
  }, [currentUserId]);

  return (
    <div className="overflow-y-auto">
      {users.map((user) => (
        <div
          key={user.id}
          className={`flex items-center p-2 cursor-pointer ${selectedUserId === user.id ? 'bg-gray-600' : 'hover:bg-gray-500'}`}
          onClick={() => selectUserToChat(user)}
        >
          <img 
            src={user.photo ? user.photo : '/Profile_images/logo2.png'} 
            alt={user.name}
            className="w-10 h-10 rounded-full mr-2"
          />
          <span className="text-white">{user.name}</span>
        </div>
      ))}
    </div>
  );
}

export default Slider;
