import React, { useState, useEffect } from 'react';
import Slider from './Slider';
import UserModal from './UserModal';
import { addDoc, collection, onSnapshot, query, where, updateDoc, doc } from 'firebase/firestore';
import { firestore } from '../firebase';

function Chatroom({ currentUser }) {
  const [selectedUser, setSelectedUser] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [pendingInvites, setPendingInvites] = useState([]);

  useEffect(() => {
    if (currentUser?.id) { // Only run if currentUser is defined and has an ID
      const q = query(
        collection(firestore, 'requests'),
        where('toUserId', '==', currentUser.id),
        where('status', '==', 'pending')
      );

      const unsubscribe = onSnapshot(q, (snapshot) => {
        const invites = snapshot.docs.map(doc => ({ id: doc.id, ...doc.data() }));
        setPendingInvites(invites);
      });

      return () => unsubscribe();
    }
  }, [currentUser]);

  const selectUserToChat = (user) => {
    setSelectedUser(user);
  };

  const sendInvitation = async (toUserId) => {
    if (!currentUser?.id) return; // Only send invitation if currentUser is defined

    try {
      await addDoc(collection(firestore, 'requests'), {
        fromUserId: currentUser.id,
        toUserId,
        status: 'pending',
        timestamp: new Date(),
      });
      alert('Invitation sent successfully.');
    } catch (error) {
      console.error('Error sending invitation:', error);
    }
  };

  const handleAcceptInvite = async (inviteId) => {
    try {
      const inviteRef = doc(firestore, 'requests', inviteId);
      await updateDoc(inviteRef, { status: 'accepted' });
      setPendingInvites((prev) => prev.filter(invite => invite.id !== inviteId));
    } catch (error) {
      console.error('Error accepting invitation:', error);
    }
  };

  if (!currentUser) return <p>Loading...</p>;

  return (
    <div className="flex">
      <div className="w-1/4">
        <Slider
          currentUserId={currentUser.id}
          selectUserToChat={selectUserToChat}
          selectedUserId={selectedUser?.id}
        />
      </div>

      <div className="w-3/4 p-4">
        {selectedUser ? (
          <div>
            <h2 className="text-xl font-bold">Chat with {selectedUser.name}</h2>
            {/* Chat messages and input go here */}
          </div>
        ) : (
          <p className="text-gray-500">Select a user to start chatting</p>
        )}

        <button
          onClick={() => setIsModalOpen(true)}
          className="bg-blue-500 text-white p-2 rounded mt-4"
        >
          Add User
        </button>

        {pendingInvites.length > 0 && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold">Pending Invitations</h3>
            {pendingInvites.map((invite) => (
              <div key={invite.id} className="flex items-center justify-between p-2 border-b">
                <span>{invite.fromUserId}</span>
                <button
                  onClick={() => handleAcceptInvite(invite.id)}
                  className="bg-green-500 text-white p-1 rounded"
                >
                  Accept
                </button>
              </div>
            ))}
          </div>
        )}
      </div>

      <UserModal
        isOpen={isModalOpen}
        onClose={() => setIsModalOpen(false)}
        onUserAdded={sendInvitation}
      />
    </div>
  );
}

export default Chatroom;
