import React, { useEffect, useState } from 'react';
import { collection, onSnapshot } from 'firebase/firestore';
import { firestore } from '../firebase'; // Ensure correct path to firebase config

function Slider({ selectUserToChat, selectedUserId }) {
  const [users, setUsers] = useState([]);

  // Fetch users from Firestore
  useEffect(() => {
    const unsubscribe = onSnapshot(collection(db, 'friends'), (snapshot) => {
      const usersData = snapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setUsers(usersData);
    });

    // Cleanup subscription on unmount
    return () => unsubscribe();
  }, []);

  return (
    <div className="flex flex-col space-y-4 p-5 bg-gradient-to-br from-blue-500 to-gray-900 rounded-lg h-full overflow-y-auto"> 
      {users && users.map((user) => (
        <div
          key={user.id}
          className={`flex items-center cursor-pointer transition-transform transform 
            ${selectedUserId === user.id ? 'shadow-lg rounded-lg shadow-blue-500' : 'hover:shadow-md'} 
            p-2 transition-all duration-200`} 
          onClick={() => selectUserToChat(user)}
          role="button"
          tabIndex={0}
          onKeyPress={(e) => e.key === 'Enter' && selectUserToChat(user)} 
          aria-label={`Select ${user.name} to chat`} // Changed to 'name'
        >
          <img 
            src={`/Profile_images/${user.photo || 'default.png'}`} // Default image if no photo is provided
            alt={user.name} // Changed to 'name'
            className="w-16 h-16 rounded-full border-2 border-gray-600 mr-4 transition-all duration-200" 
          />
          <p className="text-white text-sm truncate">{user.name}</p> {/* Changed to 'name' */}
        </div>
      ))}
    </div>
  );
}

export default Slider;
