import React, { useState, useEffect } from 'react';
import ChatMessage from './ChatMessage';
import Slider from './Slider';
import { auth } from '../firebase'; // Assuming you still need Firebase for authentication

function genID(a, b) {
  return [a, b].sort().join('_');
}

function ChatRoom() {
  const [receiverUid, setReceiverUid] = useState(null);  // For one-to-one chat
  const [formValue, setFormValue] = useState('');
  const [messages, setMessages] = useState([]);
  const [error, setError] = useState('');

  const users = [
    { id: "Zwv4kSkaQ2gW6saeexEKnbqQug73", displayName: 'Anandan', photo: 'logo1.png' },
    { id: "UluQHwfE1ec813UTVrWZHf8MXFy1", displayName: 'Shanthanu', photo: 'logo2.png' },
  ];

  // Function to register a user via API (if needed)
  const registerUser = async () => {
    try {
      const response = await fetch('http://localhost:3000/register/user', {  // Adjust the API URL accordingly
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          uuid: auth.currentUser.uid,
          email: auth.currentUser.email,
        }),
      });

      const data = await response.json();
      console.log(data.message);  // Output: user registered
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  // Function to fetch messages via API
  const fetchMessages = async () => {
    try {
      const response = await fetch(`http://localhost:3000/conversations/${genID(auth.currentUser.uid, receiverUid)}/messages`);
      const data = await response.json();
      setMessages(data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  // Function to send a message via API
  const sendMessage = async (e) => {
    e.preventDefault();

    if (!auth.currentUser || !receiverUid) {
      setError('You must select a user and be logged in to send messages.');
      return;
    }

    try {
      await fetch(`http://localhost:3000/conversations/${genID(auth.currentUser.uid, receiverUid)}/messages`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text: formValue.trim(),
          from_uid: auth.currentUser.uid,
          to_uid: receiverUid,
        }),
      });

      setFormValue('');  // Clear the input field after sending the message
      setError('');
      fetchMessages(); // Refresh messages after sending
    } catch (err) {
      console.error('Error sending message:', err);
      setError('Failed to send message. Please try again.');
    }
  };

  // Trigger fetching messages when receiverUid changes
  useEffect(() => {
    if (receiverUid) {
      fetchMessages();
    }
  }, [receiverUid]);

  return (
    <div className="flex h-screen">
      <div className="flex-none w-1/4 h-full p-5 bg-gray-800">
        <Slider 
          users={users.filter((u) => u.id !== auth.currentUser.uid)} 
          selectUserToChat={(user) => setReceiverUid(user.id)} 
          selectedUserId={receiverUid} 
        />
      </div>

      <div className="flex-grow flex flex-col pt-5 pr-5">
        <main className="flex-grow overflow-auto p-4 rounded-xl bg-gradient-to-br from-indigo-900 via-purple-900 to-gray-900 shadow-inner">
          <div className="space-y-4">
            {messages && messages.map((msg, i) => (
              <ChatMessage key={i} message={msg} />
            ))}
          </div>
        </main>

        <form 
          onSubmit={sendMessage} 
          className="bg-gray-800 p-4 mb-5 flex items-center bg-gradient-to-br via-purple-900 from-indigo-900 to-gray-900 space-x-2 rounded-lg"
        >
          <input
            value={formValue}
            onChange={(e) => setFormValue(e.target.value)}
            placeholder="Say something nice"
            className="flex-grow p-2 text-lg bg-gray-700 text-white border border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-600 shadow-inner"
          />
          <button 
            type="submit" 
            disabled={!formValue || !receiverUid}
            className="w-16 h-12 bg-blue-600 rounded-lg flex items-center justify-center text-white text-2xl shadow-lg hover:bg-blue-700 disabled:opacity-50 transition-all duration-200 transform hover:scale-105"
          >
            üïäÔ∏è
          </button>
        </form>

        {error && <p className="text-red-500 text-center mt-4">{error}</p>}
      </div>
    </div>
  );
}

export default ChatRoom;
