import React, { useState } from 'react';

function UserModal({ isOpen, onClose, onUserAdded }) {
  const [displayName, setDisplayName] = useState('Shantanu'); // To show entered displayName
  const [photo, setPhoto] = useState('/Profile_images/logo1.png'); // Default photo URL
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState('');

  const handleAddUser = async (e) => {
    e.preventDefault();
    setError('');

    // Perform your logic for checking email or adding the user, skipping Firestore storage for photo and displayName

    try {
      // This part can skip the Firestore addition if you're not saving displayName/photo
      onUserAdded(); // Notify parent component to refresh user list if needed
      setEmail('');
      setDisplayName('');
      setPhoto('/Profile_images/logo1.png'); // Reset to default photo
      onClose(); // Close the modal after user addition
    } catch (err) {
      console.error('Error adding user:', err);
      setError('Failed to add user. Please try again.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
      <div className="bg-white p-5 rounded-lg shadow-lg">
        <h2 className="text-lg font-bold">Add User</h2>
        {error && <p className="text-red-500">{error}</p>}
        <form onSubmit={handleAddUser} className="flex flex-col">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-2 mb-2 border border-gray-400 rounded text-black"
            required
          />
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => {
              setName(e.target.value);
              setDisplayName(e.target.value); // Set displayName based on the name input
            }}
            className="p-2 mb-2 border border-gray-400 rounded text-black"
            required
          />
          <img src={photo} alt="User Profile" className="w-16 h-16 rounded-full mb-2" />
          <p>{displayName || 'User'}</p>
          <button type="submit" className="bg-blue-500 text-white p-2 rounded">Add User</button>
          <button type="button" onClick={onClose} className="mt-2 text-red-500">Cancel</button>
        </form>
      </div>
    </div>
  );
}

export default UserModal;
