import React, { useState } from 'react';
import { addDoc, collection } from 'firebase/firestore';
import { firestore } from '../firebase'; // Ensure firebase is imported correctly

function UserModal({ onClose, onUserAdded }) {
  const [email, setEmail] = useState('');
  const [displayName, setDisplayName] = useState('');
  const [photo, setPhoto] = useState('logo1.png'); // Set the default photo

  const handleAddUser = async () => {
    try {
      await addDoc(collection(firestore, 'users'), {
        email,
        displayName,
        photo, // Use the photo here
      });
      onUserAdded(); // Notify parent component to refresh the user list
      setEmail('');
      setDisplayName('');
      setPhoto(''); // Reset the photo
      onClose(); // Close the modal after adding user
    } catch (err) {
      console.error('Error adding user:', err);
    }
  };

  return (
    <div className="modal">
      <h2>Add User</h2>
      <input
        type="text"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
        placeholder="Email"
      />
      <input
        type="text"
        value={displayName}
        onChange={(e) => setDisplayName(e.target.value)}
        placeholder="Display Name"
      />
      {/* You can also add an input to set a custom photo if needed */}
      <button onClick={handleAddUser}>Add User</button>
      <button onClick={onClose}>Close</button>
    </div>
  );
}

export default UserModal;
