import React, { useEffect, useState } from 'react';
import AddUserModal from './UserModal';
import { collection, getDocs } from 'firebase/firestore';
import { firestore } from '../firebase'; // Import Firestore configuration

function Slider({ users, selectUserToChat, selectedUserId }) {
  const [showModal, setShowModal] = useState(false);
  const [friends, setFriends] = useState([]);

  const handleAddUser = () => {
    setShowModal(true);
  };

  const fetchUsers = async () => {
    const userCollection = collection(firestore, 'friends');
    const userSnapshot = await getDocs(userCollection);
    const userList = userSnapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    setFriends(userList);
  };

  useEffect(() => {
    fetchUsers(); // Fetch users when the component mounts
  }, []);

  return (
    <div className="flex flex-col space-y-4 p-5 bg-gradient-to-br from-blue-500 to-gray-900 rounded-lg h-full overflow-y-auto"> 
      <div>
        <button
          className="bg-zinc-700 text-white px-4 py-2 rounded"
          onClick={handleAddUser}
        >
          Add User
        </button>

        {showModal && (
          <AddUserModal 
            setShowModal={setShowModal} 
            updateUsers={fetchUsers} // Pass the function to refresh user list
          />
        )}
      </div>
      {friends.map((user) => (
        <div
          key={user.id}
          className={`flex items-center cursor-pointer transition-transform transform 
            ${selectedUserId === user.id ? 'shadow-lg rounded-lg shadow-blue-500' : 'hover:shadow-md'} 
            p-2 transition-all duration-200`} 
          onClick={() => selectUserToChat(user)}
          role="button"
          tabIndex={0}
          onKeyPress={(e) => e.key === 'Enter' && selectUserToChat(user)} 
          aria-label={`Select ${user.displayName} to chat`}
        >
          <img 
            src={`/Profile_images/${user.photo}`} 
            alt={user.displayName} 
            className="w-16 h-16 rounded-full border-2 border-gray-600 mr-4 transition-all duration-200" 
          />
          <p className="text-white text-sm truncate">{user.displayName}</p> 
        </div>
      ))}
    </div>
  );
}

export default Slider;
