import React, { useState } from 'react'; 
import { auth } from '../firebase';
import { GoogleAuthProvider, signInWithPopup } from 'firebase/auth';
import axios from 'axios';  // Import axios for making HTTP requests

function SignIn() {
  const [errorMessage, setErrorMessage] = useState('');

  const signInWithGoogle = async () => {
    const provider = new GoogleAuthProvider();
    provider.setCustomParameters({
      prompt: 'select_account',
    });
  
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
  
      // Extract user details from Google sign-in
      const userEmail = user.email;
      const userName = user.displayName;
      const userUID = user.uid;
      const userProfilePic = user.photoURL;
      const userPassword = '';  // No password for Google sign-in but handle accordingly if needed.
  
      // Cloud Function URL to store user data in Firestore
      const functionUrl = 'https://us-central1-chatapp-a1d5f.cloudfunctions.net/userLogin';
  
      // Prepare the user data to send
      const userData = {
        email: userEmail,
        name: userName,
        uid: userUID,
        photo: userProfilePic,
        password: userPassword,  // Optional, as Google login doesn't provide a password
      };
  
      // Send the user data to Firestore via Cloud Function
      const response = await axios.post(functionUrl, userData);
  
      if (response.data.message) {
        console.log('User data successfully stored in Firestore:', response.data.message);
        // Optionally, navigate to the chat room or proceed to the next step
      }
    } catch (error) {
      if (error.code === 'auth/popup-closed-by-user') {
        setErrorMessage('The sign-in popup was closed. Please try again.');
      } else {
        setErrorMessage('An error occurred during sign-in. Please try again.');
      }
      console.error('Error during sign-in:', error);
    }
  };
  
  return (
    <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-indigo-900 via-purple-900 to-gray-900">
      <div className="bg-white bg-opacity-20 backdrop-blur-lg rounded-3xl shadow-2xl p-10 max-w-lg w-full">
        <h1 className="text-4xl font-extrabold text-white mb-6 text-center">
          Welcome to Real Chat
        </h1>
        
        <p className="text-gray-300 text-center mb-8 max-w-xs mx-auto">
          Connect and communicate with your friends privately and securely.
        </p>

        <button
          onClick={signInWithGoogle}
          className="w-full py-3 px-5 bg-blue-500 text-white font-semibold rounded-lg shadow-md hover:bg-blue-600 transition duration-300"
        >
          Sign in with Google
        </button>

        {errorMessage && (
          <p className="text-red-500 mt-4 text-center">{errorMessage}</p>
        )}

        <p className="text-gray-400 mt-6 text-center">
          Donâ€™t have an account? Sign up now.
        </p>
      </div>
    </div>
  );
}

export default SignIn;
