import React, { useState } from 'react';
import { collection, getDocs, doc, setDoc } from 'firebase/firestore';
import { firestore, auth } from '../firebase';

function UserModal({ isOpen, onClose, onInviteSent }) {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState('');

  const checkUserExists = async (email) => {
    const usersCollection = collection(firestore, 'users');
    const userDocs = await getDocs(usersCollection);
    return userDocs.docs.find(doc => doc.data().email === email);
  };

  const handleSendInvite = async (e) => {
    e.preventDefault();
    setError('');

    const userDoc = await checkUserExists(email);
    if (!userDoc) {
      setError('User not found.');
      return;
    }

    const invitedUserId = userDoc.id;
    const currentUser = auth.currentUser;
    if (!currentUser) return;

    try {
      await setDoc(doc(firestore, `users/${invitedUserId}/invites`, currentUser.uid), {
        fromUid: currentUser.uid,
        fromName: currentUser.displayName || 'Anonymous',
        status: 'pending',
      });

      onInviteSent();
      setEmail('');
      setName('');
      onClose();
    } catch (err) {
      console.error('Error sending invite:', err);
      setError('Failed to send invite. Please try again.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
      <div className="bg-white p-5 rounded-lg shadow-lg">
        <h2 className="text-lg font-bold">Invite User</h2>
        {error && <p className="text-red-500">{error}</p>}
        <form onSubmit={handleSendInvite} className="flex flex-col">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-2 mb-2 border border-gray-400 rounded text-black"
            required
          />
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="p-2 mb-2 border border-gray-400 rounded text-black"
            required
          />
          <button type="submit" className="bg-blue-500 text-white p-2 rounded">Send Invite</button>
          <button type="button" onClick={onClose} className="mt-2 text-red-500">Cancel</button>
        </form>
      </div>
    </div>
  );
}

export default UserModal;
