import React, { useState } from 'react';

function Slider({ users, selectUserToChat, selectedUserId, addUser }) {
  const [isPopupOpen, setPopupOpen] = useState(false);
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');

  const handleAddUser = () => {
    setPopupOpen(true);
  };

  const handleSaveUser = () => {
    if (email && name) {
      addUser(email, name);
      setPopupOpen(false);
      setEmail('');
      setName('');
    }
  };

  return (
    <div className="space-y-4">
      <button 
        className="w-full bg-blue-600 text-white p-2 rounded-lg hover:bg-blue-700"
        onClick={handleAddUser}
      >
        Add User
      </button>

      {/* User List */}
      {users.map((user) => (
        <div 
          key={user.id}
          onClick={() => selectUserToChat(user)}
          className={`cursor-pointer p-2 rounded-lg ${user.id === selectedUserId ? 'bg-blue-500' : 'bg-gray-800'} hover:bg-blue-700`}
        >
          <img src={user.photo || 'default.png'} alt="User" className="inline-block w-8 h-8 rounded-full mr-2" />
          {user.displayName}
        </div>
      ))}

      {/* Popup */}
      {isPopupOpen && (
        <div className="fixed inset-0 bg-gray-800 bg-opacity-50 flex justify-center items-center">
          <div className="bg-white p-5 rounded-lg">
            <h2 className="text-xl mb-4">Add New User</h2>
            <input 
              type="email" 
              placeholder="Email" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
              className="border p-2 w-full mb-4"
            />
            <input 
              type="text" 
              placeholder="Name" 
              value={name} 
              onChange={(e) => setName(e.target.value)} 
              className="border p-2 w-full mb-4"
            />
            <button 
              onClick={handleSaveUser} 
              className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
            >
              Add User
            </button>
            <button 
              onClick={() => setPopupOpen(false)} 
              className="ml-4 bg-gray-600 text-white px-4 py-2 rounded-lg hover:bg-gray-700"
            >
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Slider;
