// ChatRoom.jsx
import React, { useState, useEffect } from 'react';
import { collection, query, orderBy, limit, addDoc, serverTimestamp, getDocs } from 'firebase/firestore'; 
import { firestore, auth } from '../firebase'; 
import { useCollectionData } from 'react-firebase-hooks/firestore';
import ChatMessage from './ChatMessage';
import Slider from './Slider';
import UserModal from './UserModal';

function genID(a, b) {
  return [a, b].sort().join('_');
}

function ChatRoom() {
  const [receiverUid, setReceiverUid] = useState(null);
  const [formValue, setFormValue] = useState('');
  const [error, setError] = useState('');
  const [messagesRef, setMessagesRef] = useState(null);
  const [users, setUsers] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const currentUserUid = auth.currentUser ? auth.currentUser.uid : null;

  const [messages] = useCollectionData(messagesRef, { idField: 'id' });

  // Fetch users specific to the current authenticated user
  useEffect(() => {
    const fetchUsers = async () => {
      if (currentUserUid) {
        const usersCollection = collection(firestore, 'users');
        const userDocs = await getDocs(usersCollection);
        const usersData = userDocs.docs
          .map(doc => ({
            id: doc.id,
            ...doc.data(),
          }))
          .filter(user => user.addedBy === currentUserUid); // Adjusted to filter by user

        setUsers(usersData);
      }
    };

    fetchUsers();
  }, [currentUserUid]);

  useEffect(() => {
    if (receiverUid && currentUserUid) {
      const newMessagesRef = collection(
        firestore, 
        `conversations/${genID(currentUserUid, receiverUid)}/messages`
      );
      const q = query(newMessagesRef, orderBy('createdAt'), limit(25));
      setMessagesRef(q);
    } else {
      setMessagesRef(null);
    }
  }, [receiverUid, currentUserUid]);

  const sendMessage = async (e) => {
    e.preventDefault();

    if (!currentUserUid || !receiverUid) {
      setError('You must select a user and be logged in to send messages.');
      return;
    }

    try {
      await addDoc(collection(
        firestore, 
        `conversations/${genID(currentUserUid, receiverUid)}/messages`
      ), {
        text: formValue.trim(),
        createdAt: serverTimestamp(),
        from_uid: currentUserUid,
        to_uid: receiverUid,
      });

      setFormValue('');
      setError('');
    } catch (err) {
      console.error('Error sending message:', err);
      setError('Failed to send message. Please try again.');
    }
  };

  const handleUserAdded = async () => {
    // Refresh user list after adding a new user
    const fetchUsers = async () => {
      const usersCollection = collection(firestore, 'users');
      const userDocs = await getDocs(usersCollection);
      const usersData = userDocs.docs
        .map(doc => ({
          id: doc.id,
          ...doc.data(),
        }))
        .filter(user => user.addedBy === currentUserUid); // Adjusted to filter by user

      setUsers(usersData);
    };

    await fetchUsers();
  };

  return (
    <div className="flex h-screen">
      <div className="flex-none w-1/4 h-full p-5 bg-gray-800">
        {auth.currentUser && (
          <>
            <button 
              onClick={() => setIsModalOpen(true)} 
              className="mb-4 bg-green-500 text-white p-2 rounded">
              Add User
            </button>
            <Slider 
              users={users} // Only show users added by the current user
              selectUserToChat={(user) => setReceiverUid(user.id)} 
              selectedUserId={receiverUid} 
            />
          </>
        )}
      </div>

      <div className="flex-grow flex flex-col pt-5 pr-5">
        <main className="flex-grow overflow-auto p-4 rounded-xl bg-gradient-to-br from-indigo-900 via-purple-900 to-gray-900 shadow-inner">
          <div className="space-y-4">
            {messages && messages.map((msg, i) => (
              <ChatMessage key={i} message={msg} />
            ))}
          </div>
        </main>

        <form 
          onSubmit={sendMessage} 
          className="bg-gray-800 p-4 mb-5 flex items-center bg-gradient-to-br via-purple-900 from-indigo-900 to-gray-900 space-x-2 rounded-lg"
        >
          <input
            value={formValue}
            onChange={(e) => setFormValue(e.target.value)}
            placeholder="Say something nice"
            className="flex-grow p-2 text-lg bg-gray-700 text-white border border-gray-600 rounded-lg focus:outline-none focus:ring-2 
            focus:ring-blue-500"
          />
          <button type="submit" className="bg-blue-500 text-white p-2 rounded">
            Send
          </button>
        </form>
        {error && <p className="text-red-500">{error}</p>}
      </div>
      
      <UserModal 
        isOpen={isModalOpen} 
        onClose={() => setIsModalOpen(false)} 
        onUserAdded={handleUserAdded} 
      />
    </div>
  );
}

export default ChatRoom;
