// UserModal.jsx
import React, { useState } from 'react';
import { collection, getDocs } from 'firebase/firestore'; 
import { firestore } from '../firebase'; 

function UserModal({ isOpen, onClose, setMatchedUsers }) {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState('');

  const handleSearchUsers = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const usersCollection = collection(firestore, 'users');
      const userDocs = await getDocs(usersCollection);

      // Filter users based on email and name
      const matchedUsers = userDocs.docs
        .map(doc => ({ id: doc.id, ...doc.data() })) // include the doc ID
        .filter(user => user.email === email && user.name === name);

      if (matchedUsers.length > 0) {
        setMatchedUsers(matchedUsers); // Set matched users in parent
      } else {
        setError('No users found matching the provided email and name.');
      }

    } catch (err) {
      console.error('Error fetching users:', err);
      setError('Failed to fetch users. Please try again.');
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
      <div className="bg-white p-5 rounded-lg shadow-lg">
        <h2 className="text-lg font-bold">Search User</h2>
        {error && <p className="text-red-500">{error}</p>}
        <form onSubmit={handleSearchUsers} className="flex flex-col">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="p-2 mb-2 border border-gray-400 rounded text-black"
            required
          />
          <input
            type="text"
            placeholder="Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="p-2 mb-2 border border-gray-400 rounded text-black"
            required
          />
          <button type="submit" className="bg-blue-500 text-white p-2 rounded">Search User</button>
          <button type="button" onClick={onClose} className="mt-2 text-red-500">Cancel</button>
        </form>
      </div>
    </div>
  );
}

export default UserModal;
