import React, { useRef, useState } from 'react';

const AddUserModal = ({ setShowModal }) => {
  const emailRef = useRef();
  const passwordRef = useRef();
  const errorRef = useRef();
  const [loading, setLoading] = useState(false); // State to track loading

  const handleAddUser = async (e) => {
    e.preventDefault();

    const email = emailRef.current.value;
    const password = passwordRef.current.value;

    if (email === '' || password === '') {
      errorRef.current.textContent = 'Both fields are required';
      return;
    }

    setLoading(true); // Start loading spinner

    try {
      // Make a POST request to the Cloud Function
      const response = await fetch('https://us-central1-chatapp-a1d5f.cloudfunctions.net/userLogin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          password: password,
        }),
      });

      if (response.ok) {
        setShowModal(false); // Close modal after successful submission
      } else {
        const errorMessage = await response.text();
        errorRef.current.textContent = `Failed to add user: ${errorMessage}`;
      }
    } catch (error) {
      errorRef.current.textContent = 'Failed to add user. Try again.';
      console.error('Error adding user:', error);
    }

    setLoading(false); // Stop loading spinner
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center bg-gray-800 bg-opacity-50">
      <div className="bg-white p-6 rounded-lg shadow-lg">
        <h2 className="text-xl font-semibold mb-4">Add New User</h2>
        <form onSubmit={handleAddUser}>
          <input
            type="email"
            placeholder="Enter Email"
            ref={emailRef}
            className="border border-gray-300 p-2 w-full mb-4 rounded"
          />
          <input
            type="password"
            placeholder="Enter Password"
            ref={passwordRef}
            className="border border-gray-300 p-2 w-full mb-4 rounded text-black"
          />
          <p ref={errorRef} className="text-red-500 mb-2"></p>
          
          <button
            type="submit"
            className="bg-blue-500 text-white px-4 py-2 rounded text-black"
            disabled={loading}
          >
            {loading ? 'Adding...' : 'Add User'}
          </button>
          <button
            type="button"
            className="ml-4 bg-red-500 text-white px-4 py-2 rounded"
            onClick={() => setShowModal(false)}
            disabled={loading}
          >
            Cancel
          </button>
        </form>
      </div>
    </div>
  );
};

export default AddUserModal;
