// Slider.jsx
import React from 'react';
import logo1 from '../../public/Profile_images/logo1.png'

function Slider({ users, selectUserToChat, selectedUserId }) {
  return (
    <div className="overflow-y-auto">
      {users.map((user) => (
        <div
          key={user.id}
          className={`flex items-center p-2 cursor-pointer ${selectedUserId === user.id ? 'bg-gray-600' : 'hover:bg-gray-500'}`}
          onClick={() => selectUserToChat(user)}
        >
          {/* Use the user's photo or a default profile image */}
          <img 
            src={user.photo ? user.photo : '/public/Profile_images/logo1.png'} // Fallback to default profile image
            alt={logo1}
            className="w-10 h-10 rounded-full mr-2"
          />
          <span className="text-white">{user.name}</span>
        </div>
      ))}
    </div>
  );
}

export default Slider;
